/**
 * Get a text-set (4 sections) for a phaseKey + resultKey.
 * - phaseKey: "matching" | "firstMeet" | "date" | "relationship" | "marriage"
 * - resultKey: e.g. "D-01", "F-03", "T-12", "R-07", "M-02"
 * Returns: { scene, why, awareness, recommend }
 */
export function getTextSet(phaseKey, resultKey) {
  const phase = TEXT_BY_PHASE?.[phaseKey];
  if (!phase) {
    // Unknown phase -> empty safe default
    return emptyTextSet();
  }
  return phase[resultKey] ?? phase["_default"] ?? emptyTextSet();
}

/**
 * Convenience: render the text-set into a single string (plain text).
 * This keeps bullets as "・" lines, then two sentences.
 */
export function getResultText(phaseKey, resultKey) {
  const s = getTextSet(phaseKey, resultKey);
  return renderTextSet(s);
}

export function renderTextSet(sections) {
  const parts = [];
  parts.push(renderSection("よくあるシーン", sections.scene));
  parts.push(renderSection("なぜ起きるのか", sections.why));
  parts.push(renderSection("自覚ポイント", sections.awareness));
  parts.push(renderSection("おすすめ", sections.recommend));
  return parts.filter(Boolean).join("\n\n");
}

function renderSection(title, sec) {
  if (!sec) return "";
  const lines = [];
  lines.push(title);
  if (Array.isArray(sec.bullets)) {
    for (const b of sec.bullets) lines.push("・" + b);
  }
  if (Array.isArray(sec.sentences)) {
    for (const t of sec.sentences) if (t) lines.push(t);
  }
  // matching (only for matching phase recommend)
  if (typeof sec.matching === "string" && sec.matching.trim()) {
    lines.push(sec.matching.trim());
  }
  return lines.join("\n");
}

function emptyTextSet() {
  return {
    scene: { bullets: [], sentences: ["", ""] },
    why: { bullets: [], sentences: ["", ""] },
    awareness: { bullets: [], sentences: ["", ""] },
    recommend: { bullets: [], sentences: ["", ""] }
  };
}

export { TEXT_BY_PHASE };
